mapper.select:
select rnum, no, title, content, hit, regDate, userNo, userName, groupNo, orderNo, depth, statement from (select rownum rnum, no, title, content, hit, regDate, userNo, userName, groupNo, orderNo, depth, statement from (select r.no, r.title, r.content, r.hit, r.reg_date regDate, r.user_No userNo, u.name userName, r.group_no groupNo, r.order_no orderNo, r.depth, r.statement from rboard r, users u where r.user_No = u.no
and state != 'del' and title like '%'|| #{kwd} ||'%'
order by groupNo desc, orderNo asc ) ) where rnum>#{startRnum} and rnum<=#{endRnum}

mapper.update:

	update rboard
	set order_no = order_no+1
	where group_no = #{groupNo }
	and order_no > #{orderNo }


	update rboard 
	set hit = hit+1
	where no = #{no}

		
			update rboard
			set title=#{title },
		   		content=#{content }
			where no=#{no }
		  	and user_no = #{userNo}


	update rboard
	set state= 'del'
	where no=#{no }
  	and user_no = #{userNo}
